import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import {
  insertItemSchema,
  insertCustomerSchema,
  insertVendorSchema,
  insertStockMovementSchema,
  insertBillSchema,
  insertBillItemSchema,
  insertPurchaseSchema,
  insertPurchaseItemSchema,
} from "@shared/schema";

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // Auth routes
  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Dashboard stats
  app.get('/api/dashboard/stats', isAuthenticated, async (req, res) => {
    try {
      const stats = await storage.getDashboardStats();
      res.json(stats);
    } catch (error) {
      console.error("Error fetching dashboard stats:", error);
      res.status(500).json({ message: "Failed to fetch dashboard stats" });
    }
  });

  app.get('/api/dashboard/recent-transactions', isAuthenticated, async (req, res) => {
    try {
      const transactions = await storage.getRecentTransactions();
      res.json(transactions);
    } catch (error) {
      console.error("Error fetching recent transactions:", error);
      res.status(500).json({ message: "Failed to fetch recent transactions" });
    }
  });

  app.get('/api/dashboard/top-selling-items', isAuthenticated, async (req, res) => {
    try {
      const items = await storage.getTopSellingItems();
      res.json(items);
    } catch (error) {
      console.error("Error fetching top selling items:", error);
      res.status(500).json({ message: "Failed to fetch top selling items" });
    }
  });

  // Items routes
  app.get('/api/items', isAuthenticated, async (req, res) => {
    try {
      const { search } = req.query;
      let items;
      if (search && typeof search === 'string') {
        items = await storage.searchItems(search);
      } else {
        items = await storage.getItems();
      }
      res.json(items);
    } catch (error) {
      console.error("Error fetching items:", error);
      res.status(500).json({ message: "Failed to fetch items" });
    }
  });

  app.get('/api/items/:id', isAuthenticated, async (req, res) => {
    try {
      const item = await storage.getItem(req.params.id);
      if (!item) {
        return res.status(404).json({ message: "Item not found" });
      }
      res.json(item);
    } catch (error) {
      console.error("Error fetching item:", error);
      res.status(500).json({ message: "Failed to fetch item" });
    }
  });

  app.post('/api/items', isAuthenticated, async (req, res) => {
    try {
      const validatedData = insertItemSchema.parse(req.body);
      const item = await storage.createItem(validatedData);
      res.status(201).json(item);
    } catch (error) {
      console.error("Error creating item:", error);
      if (error && typeof error === 'object' && 'issues' in error) {
        console.error("Validation issues:", error.issues);
        res.status(400).json({ 
          message: "Invalid item data",
          validationErrors: error.issues 
        });
      } else {
        res.status(400).json({ message: "Invalid item data" });
      }
    }
  });

  app.patch('/api/items/:id', isAuthenticated, async (req, res) => {
    try {
      const item = await storage.updateItem(req.params.id, req.body);
      res.json(item);
    } catch (error) {
      console.error("Error updating item:", error);
      res.status(500).json({ message: "Failed to update item" });
    }
  });

  app.delete('/api/items/:id', isAuthenticated, async (req, res) => {
    try {
      await storage.deleteItem(req.params.id);
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting item:", error);
      res.status(500).json({ message: "Failed to delete item" });
    }
  });

  // Customers routes
  app.get('/api/customers', isAuthenticated, async (req, res) => {
    try {
      const customers = await storage.getCustomers();
      res.json(customers);
    } catch (error) {
      console.error("Error fetching customers:", error);
      res.status(500).json({ message: "Failed to fetch customers" });
    }
  });

  app.get('/api/customers/:id', isAuthenticated, async (req, res) => {
    try {
      const customer = await storage.getCustomer(req.params.id);
      if (!customer) {
        return res.status(404).json({ message: "Customer not found" });
      }
      res.json(customer);
    } catch (error) {
      console.error("Error fetching customer:", error);
      res.status(500).json({ message: "Failed to fetch customer" });
    }
  });

  app.post('/api/customers', isAuthenticated, async (req, res) => {
    try {
      const validatedData = insertCustomerSchema.parse(req.body);
      const customer = await storage.createCustomer(validatedData);
      res.status(201).json(customer);
    } catch (error) {
      console.error("Error creating customer:", error);
      res.status(400).json({ message: "Invalid customer data" });
    }
  });

  app.patch('/api/customers/:id', isAuthenticated, async (req, res) => {
    try {
      const customer = await storage.updateCustomer(req.params.id, req.body);
      res.json(customer);
    } catch (error) {
      console.error("Error updating customer:", error);
      res.status(500).json({ message: "Failed to update customer" });
    }
  });

  app.delete('/api/customers/:id', isAuthenticated, async (req, res) => {
    try {
      await storage.deleteCustomer(req.params.id);
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting customer:", error);
      res.status(500).json({ message: "Failed to delete customer" });
    }
  });

  // Vendors routes
  app.get('/api/vendors', isAuthenticated, async (req, res) => {
    try {
      const vendors = await storage.getVendors();
      res.json(vendors);
    } catch (error) {
      console.error("Error fetching vendors:", error);
      res.status(500).json({ message: "Failed to fetch vendors" });
    }
  });

  app.get('/api/vendors/:id', isAuthenticated, async (req, res) => {
    try {
      const vendor = await storage.getVendor(req.params.id);
      if (!vendor) {
        return res.status(404).json({ message: "Vendor not found" });
      }
      res.json(vendor);
    } catch (error) {
      console.error("Error fetching vendor:", error);
      res.status(500).json({ message: "Failed to fetch vendor" });
    }
  });

  app.post('/api/vendors', isAuthenticated, async (req, res) => {
    try {
      const validatedData = insertVendorSchema.parse(req.body);
      const vendor = await storage.createVendor(validatedData);
      res.status(201).json(vendor);
    } catch (error) {
      console.error("Error creating vendor:", error);
      res.status(400).json({ message: "Invalid vendor data" });
    }
  });

  app.patch('/api/vendors/:id', isAuthenticated, async (req, res) => {
    try {
      const vendor = await storage.updateVendor(req.params.id, req.body);
      res.json(vendor);
    } catch (error) {
      console.error("Error updating vendor:", error);
      res.status(500).json({ message: "Failed to update vendor" });
    }
  });

  app.delete('/api/vendors/:id', isAuthenticated, async (req, res) => {
    try {
      await storage.deleteVendor(req.params.id);
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting vendor:", error);
      res.status(500).json({ message: "Failed to delete vendor" });
    }
  });

  // Inventory routes
  app.get('/api/inventory', isAuthenticated, async (req, res) => {
    try {
      const inventory = await storage.getInventory();
      res.json(inventory);
    } catch (error) {
      console.error("Error fetching inventory:", error);
      res.status(500).json({ message: "Failed to fetch inventory" });
    }
  });

  app.get('/api/inventory/low-stock', isAuthenticated, async (req, res) => {
    try {
      const lowStockItems = await storage.getLowStockItems();
      res.json(lowStockItems);
    } catch (error) {
      console.error("Error fetching low stock items:", error);
      res.status(500).json({ message: "Failed to fetch low stock items" });
    }
  });

  app.patch('/api/inventory/:itemId', isAuthenticated, async (req, res) => {
    try {
      const { quantity } = req.body;
      if (typeof quantity !== 'number') {
        return res.status(400).json({ message: "Quantity must be a number" });
      }
      const inventory = await storage.updateInventory(req.params.itemId, quantity);
      res.json(inventory);
    } catch (error) {
      console.error("Error updating inventory:", error);
      res.status(500).json({ message: "Failed to update inventory" });
    }
  });

  // Stock movements routes
  app.get('/api/stock-movements', isAuthenticated, async (req, res) => {
    try {
      const movements = await storage.getStockMovements();
      res.json(movements);
    } catch (error) {
      console.error("Error fetching stock movements:", error);
      res.status(500).json({ message: "Failed to fetch stock movements" });
    }
  });

  app.post('/api/stock-movements', isAuthenticated, async (req, res) => {
    try {
      const validatedData = insertStockMovementSchema.parse(req.body);
      const movement = await storage.createStockMovement(validatedData);
      res.status(201).json(movement);
    } catch (error) {
      console.error("Error creating stock movement:", error);
      res.status(400).json({ message: "Invalid stock movement data" });
    }
  });

  // Bills routes
  app.get('/api/bills', isAuthenticated, async (req, res) => {
    try {
      const bills = await storage.getBills();
      res.json(bills);
    } catch (error) {
      console.error("Error fetching bills:", error);
      res.status(500).json({ message: "Failed to fetch bills" });
    }
  });

  app.get('/api/bills/:id', isAuthenticated, async (req, res) => {
    try {
      const bill = await storage.getBill(req.params.id);
      if (!bill) {
        return res.status(404).json({ message: "Bill not found" });
      }
      res.json(bill);
    } catch (error) {
      console.error("Error fetching bill:", error);
      res.status(500).json({ message: "Failed to fetch bill" });
    }
  });

  app.post('/api/bills', isAuthenticated, async (req: any, res) => {
    try {
      const { bill: billData, billItems: billItemsData } = req.body;

      // Get next bill number
      const billNumber = await storage.getNextBillNumber();

      const validatedBill = insertBillSchema.parse({
        ...billData,
        billNumber,
        createdBy: req.user.claims.sub,
      });

      const validatedBillItems = billItemsData.map((item: any) =>
        insertBillItemSchema.parse(item)
      );

      const bill = await storage.createBill(validatedBill, validatedBillItems);
      res.status(201).json(bill);
    } catch (error) {
      console.error("Error creating bill:", error);
      res.status(400).json({ message: "Invalid bill data" });
    }
  });

  app.patch('/api/bills/:id/status', isAuthenticated, async (req, res) => {
    try {
      const { status } = req.body;
      const bill = await storage.updateBillStatus(req.params.id, status);
      res.json(bill);
    } catch (error) {
      console.error("Error updating bill status:", error);
      res.status(500).json({ message: "Failed to update bill status" });
    }
  });

  app.get('/api/bills/next-number', isAuthenticated, async (req, res) => {
    try {
      const nextNumber = await storage.getNextBillNumber();
      res.json({ billNumber: nextNumber });
    } catch (error) {
      console.error("Error getting next bill number:", error);
      res.status(500).json({ message: "Failed to get next bill number" });
    }
  });

  // Purchases routes
  app.get('/api/purchases', isAuthenticated, async (req, res) => {
    try {
      const purchases = await storage.getPurchases();
      res.json(purchases);
    } catch (error) {
      console.error("Error fetching purchases:", error);
      res.status(500).json({ message: "Failed to fetch purchases" });
    }
  });

  app.get('/api/purchases/:id', isAuthenticated, async (req, res) => {
    try {
      const purchase = await storage.getPurchase(req.params.id);
      if (!purchase) {
        return res.status(404).json({ message: "Purchase not found" });
      }
      res.json(purchase);
    } catch (error) {
      console.error("Error fetching purchase:", error);
      res.status(500).json({ message: "Failed to fetch purchase" });
    }
  });

  app.post('/api/purchases', isAuthenticated, async (req: any, res) => {
    try {
      const { purchase: purchaseData, purchaseItems: purchaseItemsData } = req.body;

      // Get next purchase number
      const purchaseNumber = await storage.getNextPurchaseNumber();

      const validatedPurchase = insertPurchaseSchema.parse({
        ...purchaseData,
        purchaseNumber,
        createdBy: req.user.claims.sub,
      });

      const validatedPurchaseItems = purchaseItemsData.map((item: any) =>
        insertPurchaseItemSchema.parse(item)
      );

      const purchase = await storage.createPurchase(validatedPurchase, validatedPurchaseItems);
      res.status(201).json(purchase);
    } catch (error) {
      console.error("Error creating purchase:", error);
      res.status(400).json({ message: "Invalid purchase data" });
    }
  });

  app.get('/api/purchases/next-number', isAuthenticated, async (req, res) => {
    try {
      const nextNumber = await storage.getNextPurchaseNumber();
      res.json({ purchaseNumber: nextNumber });
    } catch (error) {
      console.error("Error getting next purchase number:", error);
      res.status(500).json({ message: "Failed to get next purchase number" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  items,
  customers,
  vendors,
  inventory,
  stockMovements,
  bills,
  billItems,
  purchases,
  purchaseItems,
  type User,
  type UpsertUser,
  type Item,
  type InsertItem,
  type ItemWithInventory,
  type Customer,
  type InsertCustomer,
  type Vendor,
  type InsertVendor,
  type Inventory,
  type InsertInventory,
  type StockMovement,
  type InsertStockMovement,
  type Bill,
  type InsertBill,
  type BillItem,
  type InsertBillItem,
  type BillWithDetails,
  type Purchase,
  type InsertPurchase,
  type PurchaseItem,
  type InsertPurchaseItem,
  type PurchaseWithDetails,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, asc, like, sql, and, lt } from "drizzle-orm";

export interface IStorage {
  // User operations (required for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;

  // Item operations
  getItems(): Promise<ItemWithInventory[]>;
  getItem(id: string): Promise<Item | undefined>;
  createItem(item: InsertItem): Promise<Item>;
  updateItem(id: string, item: Partial<InsertItem>): Promise<Item>;
  deleteItem(id: string): Promise<void>;
  searchItems(query: string): Promise<ItemWithInventory[]>;

  // Customer operations
  getCustomers(): Promise<Customer[]>;
  getCustomer(id: string): Promise<Customer | undefined>;
  createCustomer(customer: InsertCustomer): Promise<Customer>;
  updateCustomer(id: string, customer: Partial<InsertCustomer>): Promise<Customer>;
  deleteCustomer(id: string): Promise<void>;

  // Vendor operations
  getVendors(): Promise<Vendor[]>;
  getVendor(id: string): Promise<Vendor | undefined>;
  createVendor(vendor: InsertVendor): Promise<Vendor>;
  updateVendor(id: string, vendor: Partial<InsertVendor>): Promise<Vendor>;
  deleteVendor(id: string): Promise<void>;

  // Inventory operations
  getInventory(): Promise<(Inventory & { item: Item })[]>;
  getInventoryByItem(itemId: string): Promise<Inventory | undefined>;
  updateInventory(itemId: string, quantity: number): Promise<Inventory>;
  getLowStockItems(): Promise<ItemWithInventory[]>;

  // Stock movement operations
  getStockMovements(): Promise<(StockMovement & { item: Item })[]>;
  createStockMovement(movement: InsertStockMovement): Promise<StockMovement>;

  // Bill operations
  getBills(): Promise<BillWithDetails[]>;
  getBill(id: string): Promise<BillWithDetails | undefined>;
  createBill(bill: InsertBill, billItems: InsertBillItem[]): Promise<BillWithDetails>;
  updateBillStatus(id: string, status: string): Promise<Bill>;
  getNextBillNumber(): Promise<string>;

  // Purchase operations
  getPurchases(): Promise<PurchaseWithDetails[]>;
  getPurchase(id: string): Promise<PurchaseWithDetails | undefined>;
  createPurchase(purchase: InsertPurchase, purchaseItems: InsertPurchaseItem[]): Promise<PurchaseWithDetails>;
  getNextPurchaseNumber(): Promise<string>;

  // Analytics operations
  getDashboardStats(): Promise<{
    totalItems: number;
    totalCustomers: number;
    monthlySales: string;
    lowStockItems: number;
    todaysSales: string;
    billsGenerated: number;
    totalGST: string;
    pendingBills: number;
  }>;

  getRecentTransactions(): Promise<{
    type: 'sale' | 'purchase';
    amount: string;
    description: string;
    createdAt: Date;
  }[]>;

  getTopSellingItems(): Promise<{
    item: Item;
    totalSold: number;
    totalRevenue: string;
  }[]>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.email,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  // Item operations
  async getItems(): Promise<ItemWithInventory[]> {
    const result = await db
      .select({
        item: items,
        inventory: inventory,
      })
      .from(items)
      .leftJoin(inventory, eq(items.id, inventory.itemId))
      .orderBy(asc(items.name));

    return result.map(({ item, inventory: inv }) => ({
      ...item,
      inventory: inv || undefined,
      currentStock: inv?.quantity || 0,
      isLowStock: (inv?.quantity || 0) <= item.lowStockThreshold,
    }));
  }

  async getItem(id: string): Promise<Item | undefined> {
    const [item] = await db.select().from(items).where(eq(items.id, id));
    return item;
  }

  async createItem(item: InsertItem): Promise<Item> {
    const [newItem] = await db.insert(items).values(item).returning();

    // Create initial inventory entry
    await db.insert(inventory).values({
      itemId: newItem.id,
      quantity: 0,
    });

    return newItem;
  }

  async updateItem(id: string, item: Partial<InsertItem>): Promise<Item> {
    const [updatedItem] = await db
      .update(items)
      .set({ ...item, updatedAt: new Date() })
      .where(eq(items.id, id))
      .returning();
    return updatedItem;
  }

  async deleteItem(id: string): Promise<void> {
    await db.delete(items).where(eq(items.id, id));
  }

  async searchItems(query: string): Promise<ItemWithInventory[]> {
    const result = await db
      .select({
        item: items,
        inventory: inventory,
      })
      .from(items)
      .leftJoin(inventory, eq(items.id, inventory.itemId))
      .where(
        like(items.name, `%${query}%`)
      )
      .orderBy(asc(items.name));

    return result.map(({ item, inventory: inv }) => ({
      ...item,
      inventory: inv || undefined,
      currentStock: inv?.quantity || 0,
      isLowStock: (inv?.quantity || 0) <= item.lowStockThreshold,
    }));
  }

  // Customer operations
  async getCustomers(): Promise<Customer[]> {
    return await db.select().from(customers).orderBy(asc(customers.name));
  }

  async getCustomer(id: string): Promise<Customer | undefined> {
    const [customer] = await db.select().from(customers).where(eq(customers.id, id));
    return customer;
  }

  async createCustomer(customer: InsertCustomer): Promise<Customer> {
    const [newCustomer] = await db.insert(customers).values(customer).returning();
    return newCustomer;
  }

  async updateCustomer(id: string, customer: Partial<InsertCustomer>): Promise<Customer> {
    const [updatedCustomer] = await db
      .update(customers)
      .set({ ...customer, updatedAt: new Date() })
      .where(eq(customers.id, id))
      .returning();
    return updatedCustomer;
  }

  async deleteCustomer(id: string): Promise<void> {
    await db.delete(customers).where(eq(customers.id, id));
  }

  // Vendor operations
  async getVendors(): Promise<Vendor[]> {
    return await db.select().from(vendors).orderBy(asc(vendors.name));
  }

  async getVendor(id: string): Promise<Vendor | undefined> {
    const [vendor] = await db.select().from(vendors).where(eq(vendors.id, id));
    return vendor;
  }

  async createVendor(vendor: InsertVendor): Promise<Vendor> {
    const [newVendor] = await db.insert(vendors).values(vendor).returning();
    return newVendor;
  }

  async updateVendor(id: string, vendor: Partial<InsertVendor>): Promise<Vendor> {
    const [updatedVendor] = await db
      .update(vendors)
      .set({ ...vendor, updatedAt: new Date() })
      .where(eq(vendors.id, id))
      .returning();
    return updatedVendor;
  }

  async deleteVendor(id: string): Promise<void> {
    await db.delete(vendors).where(eq(vendors.id, id));
  }

  // Inventory operations
  async getInventory(): Promise<(Inventory & { item: Item })[]> {
    const result = await db
      .select({
        inventory: inventory,
        item: items,
      })
      .from(inventory)
      .innerJoin(items, eq(inventory.itemId, items.id))
      .orderBy(asc(items.name));

    return result.map(({ inventory: inv, item }) => ({
      ...inv,
      item,
    }));
  }

  async getInventoryByItem(itemId: string): Promise<Inventory | undefined> {
    const [inv] = await db.select().from(inventory).where(eq(inventory.itemId, itemId));
    return inv;
  }

  async updateInventory(itemId: string, quantity: number): Promise<Inventory> {
    const [updatedInventory] = await db
      .update(inventory)
      .set({ quantity, lastUpdated: new Date() })
      .where(eq(inventory.itemId, itemId))
      .returning();
    return updatedInventory;
  }

  async getLowStockItems(): Promise<ItemWithInventory[]> {
    const result = await db
      .select({
        item: items,
        inventory: inventory,
      })
      .from(items)
      .innerJoin(inventory, eq(items.id, inventory.itemId))
      .where(sql`${inventory.quantity} <= ${items.lowStockThreshold}`)
      .orderBy(asc(inventory.quantity));

    return result.map(({ item, inventory: inv }) => ({
      ...item,
      inventory: inv,
      currentStock: inv.quantity,
      isLowStock: true,
    }));
  }

  // Stock movement operations
  async getStockMovements(): Promise<(StockMovement & { item: Item })[]> {
    const result = await db
      .select({
        stockMovement: stockMovements,
        item: items,
      })
      .from(stockMovements)
      .innerJoin(items, eq(stockMovements.itemId, items.id))
      .orderBy(desc(stockMovements.createdAt))
      .limit(50);

    return result.map(({ stockMovement, item }) => ({
      ...stockMovement,
      item,
    }));
  }

  async createStockMovement(movement: InsertStockMovement): Promise<StockMovement> {
    const [newMovement] = await db.insert(stockMovements).values(movement).returning();

    // Update inventory quantity
    const currentInventory = await this.getInventoryByItem(movement.itemId);
    if (currentInventory) {
      let newQuantity = currentInventory.quantity;
      if (movement.type === 'purchase') {
        newQuantity += movement.quantity;
      } else if (movement.type === 'sale') {
        newQuantity -= movement.quantity;
      } else if (movement.type === 'adjustment') {
        newQuantity = movement.quantity;
      }
      await this.updateInventory(movement.itemId, Math.max(0, newQuantity));
    }

    return newMovement;
  }

  // Bill operations
  async getBills(): Promise<BillWithDetails[]> {
    const result = await db
      .select({
        bill: bills,
        customer: customers,
      })
      .from(bills)
      .innerJoin(customers, eq(bills.customerId, customers.id))
      .orderBy(desc(bills.createdAt));

    const billsWithItems = await Promise.all(
      result.map(async ({ bill, customer }) => {
        const billItemsResult = await db
          .select({
            billItem: billItems,
            item: items,
          })
          .from(billItems)
          .innerJoin(items, eq(billItems.itemId, items.id))
          .where(eq(billItems.billId, bill.id));

        return {
          ...bill,
          customer,
          billItems: billItemsResult.map(({ billItem, item }) => ({
            ...billItem,
            item,
          })),
        };
      })
    );

    return billsWithItems;
  }

  async getBill(id: string): Promise<BillWithDetails | undefined> {
    const [billResult] = await db
      .select({
        bill: bills,
        customer: customers,
      })
      .from(bills)
      .innerJoin(customers, eq(bills.customerId, customers.id))
      .where(eq(bills.id, id));

    if (!billResult) return undefined;

    const billItemsResult = await db
      .select({
        billItem: billItems,
        item: items,
      })
      .from(billItems)
      .innerJoin(items, eq(billItems.itemId, items.id))
      .where(eq(billItems.billId, id));

    return {
      ...billResult.bill,
      customer: billResult.customer,
      billItems: billItemsResult.map(({ billItem, item }) => ({
        ...billItem,
        item,
      })),
    };
  }

  async createBill(bill: InsertBill, billItemsData: InsertBillItem[]): Promise<BillWithDetails> {
    const [newBill] = await db.insert(bills).values(bill).returning();

    // Insert bill items
    const billItemsWithBillId = billItemsData.map(item => ({
      ...item,
      billId: newBill.id,
    }));
    await db.insert(billItems).values(billItemsWithBillId);

    // Create stock movements for each item
    for (const billItem of billItemsData) {
      await this.createStockMovement({
        itemId: billItem.itemId,
        type: 'sale',
        quantity: billItem.quantity,
        reason: `Sale - Bill ${newBill.billNumber}`,
      });
    }

    return this.getBill(newBill.id) as Promise<BillWithDetails>;
  }

  async updateBillStatus(id: string, status: string): Promise<Bill> {
    const [updatedBill] = await db
      .update(bills)
      .set({ status })
      .where(eq(bills.id, id))
      .returning();
    return updatedBill;
  }

  async getNextBillNumber(): Promise<string> {
    const [lastBill] = await db
      .select({ billNumber: bills.billNumber })
      .from(bills)
      .orderBy(desc(bills.createdAt))
      .limit(1);

    if (!lastBill) {
      return "INV-2024-001";
    }

    const lastNumber = parseInt(lastBill.billNumber.split('-')[2]);
    return `INV-2024-${String(lastNumber + 1).padStart(3, '0')}`;
  }

  // Purchase operations
  async getPurchases(): Promise<PurchaseWithDetails[]> {
    const result = await db
      .select({
        purchase: purchases,
        vendor: vendors,
      })
      .from(purchases)
      .innerJoin(vendors, eq(purchases.vendorId, vendors.id))
      .orderBy(desc(purchases.createdAt));

    const purchasesWithItems = await Promise.all(
      result.map(async ({ purchase, vendor }) => {
        const purchaseItemsResult = await db
          .select({
            purchaseItem: purchaseItems,
            item: items,
          })
          .from(purchaseItems)
          .innerJoin(items, eq(purchaseItems.itemId, items.id))
          .where(eq(purchaseItems.purchaseId, purchase.id));

        return {
          ...purchase,
          vendor,
          purchaseItems: purchaseItemsResult.map(({ purchaseItem, item }) => ({
            ...purchaseItem,
            item,
          })),
        };
      })
    );

    return purchasesWithItems;
  }

  async getPurchase(id: string): Promise<PurchaseWithDetails | undefined> {
    const [purchaseResult] = await db
      .select({
        purchase: purchases,
        vendor: vendors,
      })
      .from(purchases)
      .innerJoin(vendors, eq(purchases.vendorId, vendors.id))
      .where(eq(purchases.id, id));

    if (!purchaseResult) return undefined;

    const purchaseItemsResult = await db
      .select({
        purchaseItem: purchaseItems,
        item: items,
      })
      .from(purchaseItems)
      .innerJoin(items, eq(purchaseItems.itemId, items.id))
      .where(eq(purchaseItems.purchaseId, id));

    return {
      ...purchaseResult.purchase,
      vendor: purchaseResult.vendor,
      purchaseItems: purchaseItemsResult.map(({ purchaseItem, item }) => ({
        ...purchaseItem,
        item,
      })),
    };
  }

  async createPurchase(purchase: InsertPurchase, purchaseItemsData: InsertPurchaseItem[]): Promise<PurchaseWithDetails> {
    const [newPurchase] = await db.insert(purchases).values(purchase).returning();

    // Insert purchase items
    const purchaseItemsWithPurchaseId = purchaseItemsData.map(item => ({
      ...item,
      purchaseId: newPurchase.id,
    }));
    await db.insert(purchaseItems).values(purchaseItemsWithPurchaseId);

    // Create stock movements for each item
    for (const purchaseItem of purchaseItemsData) {
      await this.createStockMovement({
        itemId: purchaseItem.itemId,
        type: 'purchase',
        quantity: purchaseItem.quantity,
        reason: `Purchase - ${newPurchase.purchaseNumber}`,
      });
    }

    return this.getPurchase(newPurchase.id) as Promise<PurchaseWithDetails>;
  }

  async getNextPurchaseNumber(): Promise<string> {
    const [lastPurchase] = await db
      .select({ purchaseNumber: purchases.purchaseNumber })
      .from(purchases)
      .orderBy(desc(purchases.createdAt))
      .limit(1);

    if (!lastPurchase) {
      return "PUR-2024-001";
    }

    const lastNumber = parseInt(lastPurchase.purchaseNumber.split('-')[2]);
    return `PUR-2024-${String(lastNumber + 1).padStart(3, '0')}`;
  }

  // Analytics operations
  async getDashboardStats() {
    const [itemCount] = await db.select({ count: sql`count(*)`.mapWith(Number) }).from(items);
    const [customerCount] = await db.select({ count: sql`count(*)`.mapWith(Number) }).from(customers);

    const thirtyDaysAgo = new Date();
    thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30);

    const today = new Date();
    today.setHours(0, 0, 0, 0);

    const [monthlySalesResult] = await db
      .select({ total: sql`coalesce(sum(${bills.total}), 0)` })
      .from(bills)
      .where(and(
        sql`${bills.status} IN ('paid', 'pending')`,
        sql`${bills.billDate} >= ${thirtyDaysAgo}`
      ));

    const [todaysSalesResult] = await db
      .select({ total: sql`coalesce(sum(${bills.total}), 0)` })
      .from(bills)
      .where(and(
        sql`${bills.status} IN ('paid', 'pending')`,
        sql`${bills.billDate} >= ${today}`
      ));

    const [todaysBillsResult] = await db
      .select({ count: sql`count(*)`.mapWith(Number) })
      .from(bills)
      .where(sql`${bills.billDate} >= ${today}`);

    const [todaysGSTResult] = await db
      .select({ total: sql`coalesce(sum(${bills.gstAmount}), 0)` })
      .from(bills)
      .where(sql`${bills.billDate} >= ${today}`);

    const [pendingBillsResult] = await db
      .select({ count: sql`count(*)`.mapWith(Number) })
      .from(bills)
      .where(eq(bills.status, 'pending'));

    const lowStockItems = await this.getLowStockItems();

    return {
      totalItems: itemCount.count,
      totalCustomers: customerCount.count,
      monthlySales: (monthlySalesResult.total as string || '0'),
      lowStockItems: lowStockItems.length,
      todaysSales: (todaysSalesResult.total as string || '0'),
      billsGenerated: todaysBillsResult.count,
      totalGST: (todaysGSTResult.total as string || '0'),
      pendingBills: pendingBillsResult.count,
    };
  }

  async getRecentTransactions() {
    const salesTransactions = await db
      .select({
        id: bills.id,
        total: bills.total,
        description: sql`'Sale to ' || ${customers.name}`,
        createdAt: bills.createdAt,
        type: sql`'sale'`,
      })
      .from(bills)
      .innerJoin(customers, eq(bills.customerId, customers.id))
      .orderBy(desc(bills.createdAt))
      .limit(10);

    const purchaseTransactions = await db
      .select({
        id: purchases.id,
        total: purchases.total,
        description: sql`'Purchase from ' || ${vendors.name}`,
        createdAt: purchases.createdAt,
        type: sql`'purchase'`,
      })
      .from(purchases)
      .innerJoin(vendors, eq(purchases.vendorId, vendors.id))
      .orderBy(desc(purchases.createdAt))
      .limit(10);

    const allTransactions = [
      ...salesTransactions.map(t => ({
        type: t.type as 'sale' | 'purchase',
        amount: t.total.toString(),
        description: t.description as string,
        createdAt: t.createdAt!,
      })),
      ...purchaseTransactions.map(t => ({
        type: t.type as 'sale' | 'purchase',
        amount: t.total.toString(),
        description: t.description as string,
        createdAt: t.createdAt!,
      }))
    ];

    return allTransactions
      .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime())
      .slice(0, 10);
  }

  async getTopSellingItems() {
    const result = await db
      .select({
        item: items,
        totalSold: sql`sum(${billItems.quantity})`.mapWith(Number),
        totalRevenue: sql`sum(${billItems.amount})`,
      })
      .from(billItems)
      .innerJoin(items, eq(billItems.itemId, items.id))
      .groupBy(items.id, items.name, items.code, items.description, items.category, items.price, items.gstRate, items.unit, items.lowStockThreshold, items.createdAt, items.updatedAt)
      .orderBy(desc(sql`sum(${billItems.quantity})`))
      .limit(10);

    return result.map(({ item, totalSold, totalRevenue }) => ({
      item,
      totalSold,
      totalRevenue: (totalRevenue as string || '0'),
    }));
  }
}

export const storage = new DatabaseStorage();

