import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import StatsCard from "@/components/ui/stats-card";
import { 
  TrendingUp, 
  ShoppingCart, 
  Calculator, 
  Percent,
  BarChart3,
  PieChart,
  Download,
  Award,
  CreditCard,
  DollarSign
} from "lucide-react";

interface AnalyticsData {
  revenue: string;
  orders: number;
  avgOrderValue: string;
  profitMargin: string;
}

interface DashboardStats {
  totalItems: number;
  totalCustomers: number;
  monthlySales: string;
  lowStockItems: number;
  todaysSales: string;
  billsGenerated: number;
  totalGST: string;
  pendingBills: number;
}

interface TopSellingItem {
  item: {
    id: string;
    name: string;
    category: string;
    code: string;
  };
  totalSold: number;
  totalRevenue: string;
}

export default function Analytics() {
  const [selectedPeriod, setSelectedPeriod] = useState("last-30-days");

  const { data: dashboardStats } = useQuery<DashboardStats>({
    queryKey: ['/api/dashboard/stats'],
  });

  const { data: topSellingItems = [] } = useQuery<TopSellingItem[]>({
    queryKey: ['/api/dashboard/top-selling-items'],
  });

  // Mock analytics data - in production this would come from API
  const analyticsData: AnalyticsData = {
    revenue: dashboardStats?.monthlySales || "0",
    orders: 1234,
    avgOrderValue: "1009",
    profitMargin: "23.4",
  };

  const paymentMethods = [
    { name: "Cash", percentage: 45, color: "bg-primary" },
    { name: "UPI", percentage: 35, color: "bg-green-500" },
    { name: "Card", percentage: 20, color: "bg-blue-500" },
  ];

  const handleExportData = () => {
    // Create CSV data
    const csvData = [
      ['Metric', 'Value'],
      ['Revenue', `â‚¹${analyticsData.revenue}`],
      ['Orders', analyticsData.orders.toString()],
      ['Average Order Value', `â‚¹${analyticsData.avgOrderValue}`],
      ['Profit Margin', `${analyticsData.profitMargin}%`],
      [''],
      ['Top Selling Items', ''],
      ['Item Name', 'Quantity Sold', 'Revenue'],
      ...topSellingItems.map(item => [
        item.item.name,
        item.totalSold.toString(),
        `â‚¹${item.totalRevenue}`
      ])
    ];

    const csvContent = csvData.map(row => row.join(',')).join('\n');
    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `analytics-${selectedPeriod}-${new Date().toISOString().split('T')[0]}.csv`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
