              <SelectItem value="last-7-days">Last 7 days</SelectItem>
              <SelectItem value="last-30-days">Last 30 days</SelectItem>
              <SelectItem value="last-3-months">Last 3 months</SelectItem>
              <SelectItem value="last-year">Last year</SelectItem>
            </SelectContent>
          </Select>
          <Button
            variant="secondary"
            onClick={handleExportData}
            className="flex items-center space-x-2"
            data-testid="button-export"
          >
            <Download className="w-4 h-4" />
            <span>Export</span>
          </Button>
        </div>
      </div>

      {/* Key Metrics */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <StatsCard
          title="Revenue"
          value={`â‚¹${analyticsData.revenue}`}
          icon={TrendingUp}
          trend="+15.3% vs last month"
          trendDirection="up"
          bgColor="bg-green-500/10"
          iconColor="text-green-500"
          data-testid="stats-revenue"
        />

        <StatsCard
          title="Orders"
          value={analyticsData.orders.toString()}
          icon={ShoppingCart}
          trend="+8.7% vs last month"
          trendDirection="up"
          bgColor="bg-blue-500/10"
          iconColor="text-blue-500"
          data-testid="stats-orders"
        />

        <StatsCard
          title="Avg Order Value"
          value={`â‚¹${analyticsData.avgOrderValue}`}
          icon={Calculator}
          trend="+6.1% vs last month"
          trendDirection="up"
          bgColor="bg-purple-500/10"
          iconColor="text-purple-500"
          data-testid="stats-avg-order"
        />

        <StatsCard
          title="Profit Margin"
          value={`${analyticsData.profitMargin}%`}
          icon={Percent}
          trend="+1.2% vs last month"
          trendDirection="up"
          bgColor="bg-orange-500/10"
          iconColor="text-orange-500"
          data-testid="stats-profit-margin"
        />
      </div>

      {/* Charts */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <Card data-testid="sales-trend-chart">
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
