import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import StatsCard from "@/components/ui/stats-card";
import { 
  Package, 
  Users, 
  IndianRupee, 
  AlertTriangle,
  TrendingUp,
  TrendingDown,
  RotateCcw
} from "lucide-react";

interface DashboardStats {
  totalItems: number;
  totalCustomers: number;
  monthlySales: string;
  lowStockItems: number;
  todaysSales: string;
  billsGenerated: number;
  totalGST: string;
  pendingBills: number;
}

interface Transaction {
  type: 'sale' | 'purchase';
  amount: string;
  description: string;
  createdAt: string;
}

export default function Dashboard() {
  const { data: stats, isLoading: statsLoading } = useQuery<DashboardStats>({
    queryKey: ['/api/dashboard/stats'],
  });

  const { data: recentTransactions, isLoading: transactionsLoading } = useQuery<Transaction[]>({
    queryKey: ['/api/dashboard/recent-transactions'],
  });

  if (statsLoading) {
    return (
      <div className="p-6 space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {[...Array(4)].map((_, i) => (
            <div key={i} className="bg-card border border-border rounded-xl p-6 animate-pulse">
              <div className="h-4 bg-muted rounded mb-2"></div>
              <div className="h-8 bg-muted rounded mb-2"></div>
              <div className="h-3 bg-muted rounded w-24"></div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="p-6 space-y-6" data-testid="dashboard-content">
      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <StatsCard
          title="Total Items"
          value={stats?.totalItems.toString() || "0"}
          icon={Package}
          trend="+12% from last month"
          trendDirection="up"
          bgColor="bg-primary/10"
          iconColor="text-primary"
          data-testid="stats-total-items"
        />

        <StatsCard
          title="Active Customers"
          value={stats?.totalCustomers.toString() || "0"}
          icon={Users}
          trend="+5% from last month"
          trendDirection="up"
          bgColor="bg-blue-500/10"
          iconColor="text-blue-500"
          data-testid="stats-active-customers"
        />

        <StatsCard
          title="Monthly Sales"
          value={`â‚¹${stats?.monthlySales || "0"}`}
          icon={IndianRupee}
          trend="+18% from last month"
          trendDirection="up"
          bgColor="bg-green-500/10"
          iconColor="text-green-500"
          data-testid="stats-monthly-sales"
        />

        <StatsCard
          title="Low Stock Items"
          value={stats?.lowStockItems.toString() || "0"}
          icon={AlertTriangle}
          trend="Requires attention"
          trendDirection="neutral"
          bgColor="bg-destructive/10"
          iconColor="text-destructive"
          data-testid="stats-low-stock"
        />
      </div>

      {/* Charts and Recent Activity */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <Card data-testid="sales-trend-chart">
          <CardHeader>
            <CardTitle>Sales Trend</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="h-64 bg-muted/20 rounded-lg flex items-center justify-center">
              <p className="text-muted-foreground">Chart implementation coming soon</p>
            </div>
          </CardContent>
        </Card>

        <Card data-testid="recent-transactions">
          <CardHeader>
            <CardTitle>Recent Transactions</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {transactionsLoading ? (
                <div className="space-y-3">
                  {[...Array(3)].map((_, i) => (
                    <div key={i} className="flex items-center justify-between p-3 bg-muted/20 rounded-lg animate-pulse">
                      <div className="flex items-center space-x-3">
                        <div className="w-8 h-8 bg-muted rounded-lg"></div>
                        <div>
                          <div className="h-4 bg-muted rounded w-32 mb-1"></div>
                          <div className="h-3 bg-muted rounded w-16"></div>
                        </div>
                      </div>
                      <div className="h-4 bg-muted rounded w-16"></div>
                    </div>
                  ))}
                </div>
              ) : recentTransactions && recentTransactions.length > 0 ? (
                recentTransactions.slice(0, 5).map((transaction, index) => (
                  <div key={index} className="flex items-center justify-between p-3 bg-muted/20 rounded-lg" data-testid={`transaction-${index}`}>
                    <div className="flex items-center space-x-3">
                      <div className={`p-2 rounded-lg ${transaction.type === 'sale' ? 'bg-green-500/10' : 'bg-red-500/10'}`}>
                        {transaction.type === 'sale' ? (
                          <TrendingUp className="w-4 h-4 text-green-500" />
                        ) : (
                          <TrendingDown className="w-4 h-4 text-red-500" />
                        )}
                      </div>
                      <div>
                        <p className="font-medium text-sm">{transaction.description}</p>
                        <p className="text-xs text-muted-foreground">
                          {new Date(transaction.createdAt).toLocaleDateString()}
                        </p>
                      </div>
                    </div>
                    <p className={`font-medium ${transaction.type === 'sale' ? 'text-green-600' : 'text-red-600'}`}>
                      {transaction.type === 'sale' ? '+' : '-'}â‚¹{transaction.amount}
                    </p>
                  </div>
                ))
              ) : (
                <div className="flex items-center justify-center p-8 text-muted-foreground">
                  <div className="text-center">
                    <RotateCcw className="w-8 h-8 mx-auto mb-2 opacity-50" />
                    <p>No recent transactions</p>
                  </div>
                </div>
              )}
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/inventory.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";
import StockModal from "@/components/modals/stock-modal";
import LowStockAlert from "@/components/ui/low-stock-alert";
import { 
  Package, 
  Search, 
  Edit, 
  Plus, 
  AlertTriangle,
  CheckCircle,
  XCircle,
  TrendingUp,
  TrendingDown,
  RotateCcw
} from "lucide-react";
import type { ItemWithInventory, StockMovement } from "@shared/schema";

interface InventoryWithItem {
  id: string;
  itemId: string;
  quantity: number;
  lastUpdated: string;
  item: {
    id: string;
    code: string;
    name: string;
    category: string;
    price: string;
    lowStockThreshold: number;
  };
}

interface StockMovementWithItem extends StockMovement {
  item: {
    id: string;
    code: string;
    name: string;
  };
}

export default function Inventory() {
  const [searchQuery, setSearchQuery] = useState("");
  const [isStockModalOpen, setIsStockModalOpen] = useState(false);
  const [selectedItem, setSelectedItem] = useState<ItemWithInventory | null>(null);
  const [modalType, setModalType] = useState<'add' | 'update'>('add');
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: inventory = [], isLoading: inventoryLoading } = useQuery<InventoryWithItem[]>({
    queryKey: ['/api/inventory'],
  });

  const { data: lowStockItems = [], isLoading: lowStockLoading } = useQuery<ItemWithInventory[]>({
    queryKey: ['/api/inventory/low-stock'],
  });

  const { data: stockMovements = [], isLoading: movementsLoading } = useQuery<StockMovementWithItem[]>({
    queryKey: ['/api/stock-movements'],
  });

  const updateInventoryMutation = useMutation({
    mutationFn: async ({ itemId, quantity }: { itemId: string; quantity: number }) => {
      await apiRequest('PATCH', `/api/inventory/${itemId}`, { quantity });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/inventory'] });
      queryClient.invalidateQueries({ queryKey: ['/api/inventory/low-stock'] });
      queryClient.invalidateQueries({ queryKey: ['/api/stock-movements'] });
      toast({
        title: "Success",
        description: "Inventory updated successfully",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to update inventory",
        variant: "destructive",
      });
    },
  });

  const filteredInventory = inventory.filter(inv =>
    inv.item.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    inv.item.code.toLowerCase().includes(searchQuery.toLowerCase()) ||
    inv.item.category.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const handleAddStock = () => {
    setSelectedItem(null);
    setModalType('add');
    setIsStockModalOpen(true);
  };

  const handleUpdateStock = (inventoryItem: InventoryWithItem) => {
    const itemWithInventory: ItemWithInventory = {
      ...inventoryItem.item,
      inventory: {
        id: inventoryItem.id,
        itemId: inventoryItem.itemId,
        quantity: inventoryItem.quantity,
        lastUpdated: inventoryItem.lastUpdated,
      },
      currentStock: inventoryItem.quantity,
      isLowStock: inventoryItem.quantity <= inventoryItem.item.lowStockThreshold,
    };
    setSelectedItem(itemWithInventory);
    setModalType('update');
    setIsStockModalOpen(true);
  };

  const getStockStatus = (quantity: number, threshold: number) => {
    if (quantity === 0) return { status: 'out', color: 'text-red-600', bg: 'bg-red-500/10', icon: XCircle };
    if (quantity <= threshold) return { status: 'low', color: 'text-yellow-600', bg: 'bg-yellow-500/10', icon: AlertTriangle };
    return { status: 'good', color: 'text-green-600', bg: 'bg-green-500/10', icon: CheckCircle };
  };

  const getMovementIcon = (type: string) => {
    switch (type) {
      case 'purchase':
        return <TrendingUp className="w-4 h-4 text-green-500" />;
      case 'sale':
        return <TrendingDown className="w-4 h-4 text-red-500" />;
      case 'adjustment':
        return <RotateCcw className="w-4 h-4 text-blue-500" />;
      default:
        return <Package className="w-4 h-4 text-muted-foreground" />;
    }
  };

  const getMovementColor = (type: string) => {
    switch (type) {
      case 'purchase':
        return 'bg-green-500/10';
      case 'sale':
        return 'bg-red-500/10';
      case 'adjustment':
        return 'bg-blue-500/10';
      default:
        return 'bg-muted/20';
    }
  };

  const calculateTotalValue = () => {
    return inventory.reduce((total, inv) => {
      return total + (inv.quantity * parseFloat(inv.item.price));
    }, 0);
  };

  if (inventoryLoading) {
    return (
      <div className="p-6 space-y-6">
        <div className="animate-pulse">
          <div className="h-8 bg-muted rounded w-64 mb-2"></div>
          <div className="h-4 bg-muted rounded w-48 mb-6"></div>
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {[...Array(4)].map((_, i) => (
              <div key={i} className="bg-card border border-border rounded-xl p-6">
                <div className="h-6 bg-muted rounded mb-4"></div>
                <div className="h-32 bg-muted rounded"></div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6 space-y-6" data-testid="inventory-page">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold" data-testid="page-title">Inventory Management</h2>
          <p className="text-muted-foreground">Track and manage your stock levels</p>
        </div>
        <div className="flex space-x-2">
          <Button
            variant="secondary"
            onClick={() => handleUpdateStock(inventory[0])}
            className="flex items-center space-x-2"
            disabled={inventory.length === 0}
            data-testid="button-update-stock"
          >
            <Edit className="w-4 h-4" />
            <span>Update Stock</span>
          </Button>
          <Button
            onClick={handleAddStock}
            className="flex items-center space-x-2"
            data-testid="button-add-stock"
          >
            <Plus className="w-4 h-4" />
            <span>Add Stock</span>
          </Button>
        </div>
      </div>

      {/* Low Stock Alerts */}
      {!lowStockLoading && lowStockItems.length > 0 && (
        <LowStockAlert items={lowStockItems} />
      )}

      {/* Search */}
      <Card>
        <CardContent className="p-4">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
            <Input
              placeholder="Search inventory..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-10"
              data-testid="input-search-inventory"
            />
          </div>
        </CardContent>
      </Card>

      {/* Inventory Overview */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <Card data-testid="stock-summary">
          <CardHeader>
            <CardTitle>Stock Summary</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="flex justify-between items-center">
                <div className="flex items-center space-x-3">
                  <div className="bg-green-500/10 p-2 rounded-lg">
                    <CheckCircle className="w-5 h-5 text-green-500" />
                  </div>
                  <span>In Stock Items</span>
                </div>
                <span className="font-semibold" data-testid="in-stock-count">
                  {inventory.filter(inv => inv.quantity > inv.item.lowStockThreshold).length}
                </span>
              </div>
              <div className="flex justify-between items-center">
                <div className="flex items-center space-x-3">
                  <div className="bg-yellow-500/10 p-2 rounded-lg">
                    <AlertTriangle className="w-5 h-5 text-yellow-500" />
                  </div>
                  <span>Low Stock Items</span>
                </div>
                <span className="font-semibold text-yellow-600" data-testid="low-stock-count">
                  {lowStockItems.length}
                </span>
              </div>
              <div className="flex justify-between items-center">
                <div className="flex items-center space-x-3">
                  <div className="bg-red-500/10 p-2 rounded-lg">
                    <XCircle className="w-5 h-5 text-red-500" />
                  </div>
                  <span>Out of Stock Items</span>
                </div>
                <span className="font-semibold text-red-600" data-testid="out-of-stock-count">
                  {inventory.filter(inv => inv.quantity === 0).length}
                </span>
              </div>
              <div className="flex justify-between items-center pt-2 border-t border-border">
                <span className="font-medium">Total Inventory Value</span>
                <span className="font-bold text-lg" data-testid="total-inventory-value">
                  â‚¹{calculateTotalValue().toLocaleString()}
                </span>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card data-testid="recent-movements">
          <CardHeader>
            <CardTitle>Recent Stock Movements</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {movementsLoading ? (
                <div className="space-y-3">
                  {[...Array(3)].map((_, i) => (
                    <div key={i} className="flex items-center justify-between p-3 bg-muted/20 rounded-lg animate-pulse">
                      <div className="flex items-center space-x-3">
                        <div className="w-8 h-8 bg-muted rounded-lg"></div>
                        <div>
                          <div className="h-4 bg-muted rounded w-32 mb-1"></div>
                          <div className="h-3 bg-muted rounded w-24"></div>
                        </div>
                      </div>
                      <div className="h-3 bg-muted rounded w-12"></div>
                    </div>
                  ))}
                </div>
              ) : stockMovements.length > 0 ? (
                stockMovements.slice(0, 5).map((movement, index) => (
                  <div key={movement.id} className={`flex items-center justify-between p-3 rounded-lg ${getMovementColor(movement.type)}`} data-testid={`movement-${index}`}>
                    <div className="flex items-center space-x-3">
                      <div className="p-2 rounded-lg bg-background/50">
                        {getMovementIcon(movement.type)}
                      </div>
                      <div>
                        <p className="font-medium text-sm capitalize">
                          {movement.type === 'purchase' ? 'Stock Added' : 
                           movement.type === 'sale' ? 'Sale' : 'Stock Adjustment'}
                        </p>
                        <p className="text-xs text-muted-foreground">
                          {movement.item.name} ({movement.type === 'sale' ? '-' : '+'}{movement.quantity})
                        </p>
                      </div>
                    </div>
                    <p className="text-xs text-muted-foreground">
                      {new Date(movement.createdAt!).toLocaleDateString()}
                    </p>
                  </div>
                ))
              ) : (
                <div className="flex items-center justify-center p-8 text-muted-foreground">
                  <div className="text-center">
                    <Package className="w-8 h-8 mx-auto mb-2 opacity-50" />
                    <p>No stock movements yet</p>
                  </div>
                </div>
              )}
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Inventory Table */}
      <Card data-testid="inventory-table">
        <CardHeader>
          <CardTitle>Inventory Details</CardTitle>
        </CardHeader>
        <CardContent className="p-0">
          {filteredInventory.length === 0 ? (
            <div className="flex items-center justify-center p-12">
              <div className="text-center">
                <Package className="w-12 h-12 mx-auto mb-4 text-muted-foreground opacity-50" />
                <h3 className="text-lg font-semibold mb-2">No inventory found</h3>
                <p className="text-muted-foreground">
                  {searchQuery ? "Try adjusting your search criteria" : "Start by adding some stock"}
                </p>
              </div>
            </div>
          ) : (
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead className="bg-muted/50">
                  <tr>
                    <th className="text-left p-4 font-medium text-muted-foreground">Item</th>
                    <th className="text-left p-4 font-medium text-muted-foreground">Category</th>
                    <th className="text-left p-4 font-medium text-muted-foreground">Current Stock</th>
                    <th className="text-left p-4 font-medium text-muted-foreground">Status</th>
                    <th className="text-left p-4 font-medium text-muted-foreground">Value</th>
                    <th className="text-left p-4 font-medium text-muted-foreground">Last Updated</th>
                    <th className="text-left p-4 font-medium text-muted-foreground">Actions</th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-border">
                  {filteredInventory.map((inv) => {
                    const stockStatus = getStockStatus(inv.quantity, inv.item.lowStockThreshold);
                    const StatusIcon = stockStatus.icon;

                    return (
                      <tr key={inv.id} className="hover:bg-muted/20" data-testid={`inventory-row-${inv.item.code}`}>
                        <td className="p-4">
                          <div>
                            <p className="font-medium" data-testid={`item-name-${inv.item.code}`}>{inv.item.name}</p>
                            <p className="text-sm text-muted-foreground font-mono" data-testid={`item-code-${inv.item.code}`}>{inv.item.code}</p>
                          </div>
                        </td>
                        <td className="p-4">
                          <Badge variant="secondary" data-testid={`item-category-${inv.item.code}`}>
                            {inv.item.category}
                          </Badge>
                        </td>
                        <td className="p-4">
                          <span className="font-semibold text-lg" data-testid={`current-stock-${inv.item.code}`}>
                            {inv.quantity}
                          </span>
                        </td>
                        <td className="p-4">
                          <div className={`flex items-center space-x-2 px-2 py-1 rounded-lg ${stockStatus.bg}`}>
                            <StatusIcon className={`w-4 h-4 ${stockStatus.color}`} />
                            <span className={`text-sm font-medium ${stockStatus.color}`} data-testid={`stock-status-${inv.item.code}`}>
                              {stockStatus.status === 'out' ? 'Out of Stock' :
                               stockStatus.status === 'low' ? 'Low Stock' : 'In Stock'}
                            </span>
                          </div>
                        </td>
                        <td className="p-4">
                          <span className="font-medium" data-testid={`stock-value-${inv.item.code}`}>
                            â‚¹{(inv.quantity * parseFloat(inv.item.price)).toLocaleString()}
                          </span>
                        </td>
                        <td className="p-4">
                          <span className="text-sm text-muted-foreground" data-testid={`last-updated-${inv.item.code}`}>
                            {new Date(inv.lastUpdated).toLocaleDateString()}
                          </span>
                        </td>
                        <td className="p-4">
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => handleUpdateStock(inv)}
                            className="h-8 w-8 p-0"
                            title="Update Stock"
                            data-testid={`button-update-stock-${inv.item.code}`}
                          >
                            <Edit className="w-4 h-4" />
                          </Button>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          )}
        </CardContent>
      </Card>

      <StockModal
        isOpen={isStockModalOpen}
        onClose={() => setIsStockModalOpen(false)}
        item={selectedItem}
        type={modalType}
      />
    </div>
  );
}

client/src/pages/items.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";
import ItemModal from "@/components/modals/item-modal";
import { Plus, Search, Edit, Eye, Trash2, Package } from "lucide-react";
import type { ItemWithInventory } from "@shared/schema";

export default function Items() {
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editingItem, setEditingItem] = useState<ItemWithInventory | null>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: items = [], isLoading } = useQuery<ItemWithInventory[]>({
    queryKey: ['/api/items'],
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest('DELETE', `/api/items/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/items'] });
      toast({
        title: "Success",
        description: "Item deleted successfully",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to delete item",
        variant: "destructive",
      });
    },
  });

  const filteredItems = items.filter(item => {
    const matchesSearch = item.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         item.code.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesCategory = !selectedCategory || selectedCategory === "__all__" || item.category === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  const categories = Array.from(new Set(items.map(item => item.category).filter(category => category && category.trim() !== "")));

  const handleAddItem = () => {
    setEditingItem(null);
    setIsModalOpen(true);
  };

  const handleEditItem = (item: ItemWithInventory) => {
    setEditingItem(item);
    setIsModalOpen(true);
  };

  const handleDeleteItem = (id: string) => {
    if (confirm("Are you sure you want to delete this item?")) {
      deleteMutation.mutate(id);
    }
  };

  if (isLoading) {
    return (
      <div className="p-6 space-y-6">
        <div className="animate-pulse">
          <div className="h-8 bg-muted rounded w-64 mb-2"></div>
          <div className="h-4 bg-muted rounded w-48 mb-6"></div>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {[...Array(6)].map((_, i) => (
              <div key={i} className="bg-card border border-border rounded-xl p-6">
                <div className="h-6 bg-muted rounded mb-4"></div>
                <div className="h-4 bg-muted rounded mb-2"></div>
                <div className="h-4 bg-muted rounded w-3/4"></div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6 space-y-6" data-testid="items-page">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold" data-testid="page-title">Items Management</h2>
          <p className="text-muted-foreground">Manage your product catalog</p>
        </div>
        <Button onClick={handleAddItem} className="flex items-center space-x-2" data-testid="button-add-item">
          <Plus className="w-4 h-4" />
          <span>Add New Item</span>
        </Button>
      </div>

      {/* Search and Filter */}
      <Card>
        <CardContent className="p-4">
          <div className="flex flex-col md:flex-row gap-4">
            <div className="flex-1 relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
              <Input
                placeholder="Search items..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-10"
                data-testid="input-search-items"
              />
            </div>
            <Select value={selectedCategory} onValueChange={setSelectedCategory}>
              <SelectTrigger className="w-full md:w-48" data-testid="select-category-filter">
                <SelectValue placeholder="All Categories" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="__all__">All Categories</SelectItem>
                {categories.map(category => (
                  <SelectItem key={category} value={category}>{category}</SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>
        </CardContent>
      </Card>

      {/* Items Grid */}
      {filteredItems.length === 0 ? (
        <Card>
          <CardContent className="flex items-center justify-center p-12">
            <div className="text-center">
              <Package className="w-12 h-12 mx-auto mb-4 text-muted-foreground opacity-50" />
              <h3 className="text-lg font-semibold mb-2">No items found</h3>
              <p className="text-muted-foreground mb-4">
                {searchQuery || selectedCategory ? "Try adjusting your search criteria" : "Get started by adding your first item"}
              </p>
              {!searchQuery && !selectedCategory && (
                <Button onClick={handleAddItem} data-testid="button-add-first-item">
                  <Plus className="w-4 h-4 mr-2" />
                  Add Your First Item
                </Button>
              )}
            </div>
          </CardContent>
        </Card>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredItems.map((item) => (
            <Card key={item.id} className="hover:shadow-md transition-shadow" data-testid={`item-card-${item.code}`}>
              <CardContent className="p-6">
                <div className="flex items-start justify-between mb-4">
                  <div className="flex-1">
                    <h3 className="font-semibold mb-1" data-testid={`item-name-${item.code}`}>{item.name}</h3>
                    <p className="text-sm text-muted-foreground font-mono mb-2" data-testid={`item-code-${item.code}`}>{item.code}</p>
                    <Badge variant="secondary" className="text-xs" data-testid={`item-category-${item.code}`}>
                      {item.category}
                    </Badge>
                  </div>
                  <div className="flex space-x-1">
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => handleEditItem(item)}
                      className="h-8 w-8 p-0"
                      title="Edit"
                      data-testid={`button-edit-${item.code}`}
                    >
                      <Edit className="w-4 h-4" />
                    </Button>
                    <Button
                      variant="ghost"
                      size="sm"
                      className="h-8 w-8 p-0"
                      title="View"
                      data-testid={`button-view-${item.code}`}
                    >
                      <Eye className="w-4 h-4" />
                    </Button>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => handleDeleteItem(item.id)}
                      className="h-8 w-8 p-0 text-destructive hover:text-destructive"
                      title="Delete"
                      data-testid={`button-delete-${item.code}`}
                    >
                      <Trash2 className="w-4 h-4" />
                    </Button>
                  </div>
                </div>

                <div className="space-y-2 text-sm">
                  <div className="flex justify-between">
                    <span className="text-muted-foreground">Price:</span>
                    <span className="font-medium" data-testid={`item-price-${item.code}`}>â‚¹{item.price}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-muted-foreground">Stock:</span>
                    <Badge 
                      variant={item.isLowStock ? "destructive" : "default"}
                      className="text-xs"
                      data-testid={`item-stock-${item.code}`}
                    >
                      {item.isLowStock ? `Low Stock (${item.currentStock})` : `In Stock (${item.currentStock})`}
                    </Badge>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-muted-foreground">GST Rate:</span>
                    <span className="font-medium" data-testid={`item-gst-${item.code}`}>{item.gstRate}%</span>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}

      <ItemModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        item={editingItem}
      />
    </div>
  );
}

